		name:		'lines',

    properties : {},
    
    retrigger: function() {
      phase = 0
      out = values[0]
      targetTime = times[ 0 ]
      targetValue = values[ 1 ]
      valuesPhase = 1
      timesPhase = 0
      incr = ( targetValue - out ) / targetTime
      end = false
    },
    
